SELECT /* daObjectMapper.xml/getTableList by rohys03 */ /*+ LEADING(D S T) USE_NL(T) */ D.DB_NM dbName, T.DB_ID dbId, T.OWNER owner, T.TABLE_NAME tableName, T.LOGIC_AREA_CD logicalAreaCd, T.LOGIC_AREA_CD2 logicalAreaCd2, T.PHSCL_AREA_CD physicalAreaCd, T.TABLE_COMMENTS tableComments, T.TABLE_CREATE_DT tableCreateDt, T.STD_OBJ_YN standardObjectYn, S.SUBJ_AREA_CD subjAreaCd, S.HGRNK_SUBJ_AREA_CD hgrnkSubjAreaCd From DAUSER.DA_DB_LIST D, DAUSER.DA_TABLES T, DAUSER.DA_TAB_SUBJ_AREA S WHERE D.DB_ID = T.DB_ID AND T.SUBJ_AREA_CD = S.SUBJ_AREA_CD
and T.DB_ID = #{dbId}
and T.DB_NM = #{dbName}
and T.TABLE_NAME like '%'|| #{tableName} || '%'
and (S.SUBJ_AREA_CD = #{subjAreaCd} or S.HGRNK_SUBJ_AREA_CD = #{subjAreaCd})
ORDER BY D.DB_ID, S.HGRNK_SUBJ_AREA_CD nulls last, S.SUBJ_AREA_CD nulls first, T.TABLE_NAME ASC
