Select /* daSqlMapper.xml/getSqlNameList - ns: by rohys03 */ /*+ ORDERED USE_NL(L S) INDEX(L PK_DA_SQLNAME_LIST) */ L.SQL_NAME sqlName, L.REP_SQL_ID repSqlId, L.Parsing_schema_name parsingSchemaName, l.Module module, l.Last_active_time LastActiveTime, S.CLCT_DY clctDy, S.DB_ID dbId, S.SQL_NAME_NO sqlNameNo, S.CURR_TOTAL_EXEC currTotalExec, S.PREV_TOTAL_EXEC prevTotalExec, S.EXEC_DIFF execDiff, S.CURR_TOTAL_BGET currTotalBget, S.PREV_TOTAL_BGET prevTotalBget, S.BGET_DIFF bgetDiff, S.CURR_TOTAL_CPU currTotalCpu, S.PREV_TOTAL_CPU prevTotalCpu, S.CPU_DIFF cpuDiff, S.CURR_TOTAL_ELA currTotalElap, S.PREV_TOTAL_ELA prevTotalElap, S.ELA_DIFF elapDiff, S.CURR_CLCT_DY currClctDy, S.PREV_CLCT_DY prevClctDy, S.CREATE_DT createDt From dauser.da_sqlname_list L, dauser.da_sqlname_stats s where 1=1 and l.SQL_NAME_NO = s.SQL_NAME_NO and l.db_id = s.db_id and s.CLCT_DY = #{clctDy}
AND S.SQL_NAME_NO in (select sql_name_no from dauser.da_sqlname_list where sql_name like '%'||#{sqlName}||'%' )
and l.sql_name_no in ( select /*+ leading(T) use_nl(Info) index(INFO IX1_DA_SQLNAME_DTL_INFO) */ info.sql_name_no From DAuser.DA_SQLNAME_DTL_INFO Info, dauser.da_tables t where info.owner = t.owner and info.object_name = t.table_name and object_type = 'TABLE'
and T.LOGIC_AREA_CD = #{logicalAreaCd1}
and T.LOGIC_AREA_CD2 = #{logicalAreaCd2}
) ORDER BY L.SQL_NAME, L.DB_ID, S.CLCT_DY desc

SELECT /* daSqlMapper.xml/getSqlNameStats - ns: by rohys03 */ CLCT_DY clctDy , DB_ID dbId , SQL_NAME_NO sqlNameNo , CURR_TOTAL_EXEC currTotalExec , PREV_TOTAL_EXEC prevTotalExec , EXEC_DIFF execDiff , CURR_TOTAL_BGET currTotalBget , PREV_TOTAL_BGET prevTotalBget , BGET_DIFF bgetDiff , CURR_TOTAL_CPU currTotalCpu , PREV_TOTAL_CPU prevTotalCpu , CPU_DIFF cpuDiff , CURR_TOTAL_ELA currTotalElap , PREV_TOTAL_ELA prevTotalElap , ELA_DIFF elapDiff , CURR_CLCT_DY currClctDy , PREV_CLCT_DY prevClctDy , CREATE_DT createDt FROM DAUSER.DA_SQLNAME_STATS S WHERE 1=1
and s.CLCT_DY = #{clctDy}
AND S.SQL_NAME_NO in (select sql_name_no from dauser.da_sqlname_list where sql_name like '%'||#{sqlName}||'%' ) Order by CLCT_DY desc
